import React, { useState } from 'react';
import { View, Text, TextInput, TouchableOpacity, Image, StyleSheet } from 'react-native';
import { getFirestore, collection, addDoc } from 'firebase/firestore';
import { useNavigation } from '@react-navigation/native';
import { firebase } from '../config';
import { storage } from '../config';
import ImagePicker from 'react-native-image-picker';

const Registration = () => {
  const [cidade, setCidade] = useState('');
  const [descricao, setDescricao] = useState('');
  const [telefone, setTelefone] = useState('');
  const [imagem, setImagem] = useState(null);

  const navigation = useNavigation();

  const handleCadastro = async () => {
    const db = getFirestore();
    try {
      await addDoc(collection(db, 'idrmp43D1TEUCWZWEHci'), {
        cidade,
        descricao,
        telefone,
        imagemUri: imagem, // Salvar a URI da imagem no banco de dados
        userEmail: firebase.auth().currentUser.email,
      });
      console.log('Documento adicionado com sucesso!');
      // Limpar campos após o cadastro
      setCidade('');
      setDescricao('');
      setTelefone('');
      setImagem(null);

      // Navegar para a tela de informações e passar os dados como parâmetros
      navigation.navigate('Dashboard');
    } catch (error) {
      console.error('Erro ao adicionar documento: ', error);
    }
  };

  const handleImagemSelecionada = () => {
    const options = {
      title: 'Selecione uma imagem',
      storageOptions: {
        skipBackup: true,
        path: 'images',
      },
    };

    ImagePicker.showImagePicker(options, (response) => {
      if (response.didCancel) {
        console.log('Seleção de imagem cancelada');
      } else if (response.error) {
        console.log('Erro ao selecionar imagem: ', response.error);
      } else {
        // Defina a imagem selecionada no estado
        setImagem(response.uri);
      }
    });
  };

  return (
    <View style={styles.container}>
      <Text style={styles.heading}>Cadastro de Informações</Text>
      <TextInput
        style={styles.input}
        placeholder="Cidade"
        placeholderTextColor="#fff"
        value={cidade}
        onChangeText={setCidade}
      />
      <TextInput
        style={styles.input}
        placeholder="Descrição"
        placeholderTextColor="#fff"
        value={descricao}
        onChangeText={setDescricao}
      />
      <TextInput
        style={styles.input}
        placeholder="Telefone"
        placeholderTextColor="#fff"
        value={telefone}
        onChangeText={setTelefone}
        keyboardType="numeric"
      />
      {imagem && <Image source={{ uri: imagem }} style={styles.imagem} />}
      <TouchableOpacity style={styles.button} onPress={handleImagemSelecionada}>
        <Text style={styles.buttonText}>Selecionar Imagem</Text>
      </TouchableOpacity>
      <TouchableOpacity style={styles.button} onPress={handleCadastro}>
        <Text style={styles.buttonText}>Cadastrar</Text>
      </TouchableOpacity>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: '#000',
  },
  heading: {
    fontSize: 24,
    marginBottom: 20,
    color: '#fff',
  },
  input: {
    width: '80%',
    height: 40,
    borderColor: 'gray',
    borderWidth: 1,
    marginBottom: 10,
    padding: 10,
    color: '#fff',
  },
  button: {
    backgroundColor: '#fff',
    borderRadius: 25,
    width: '60%',
    height: 50,
    alignItems: 'center',
    justifyContent: 'center',
    marginTop: 20,
  },
  buttonText: {
    fontWeight: 'bold',
    fontSize: 18,
    color: '#000',
  },
  imagem: {
    width: 200,
    height: 200,
    marginBottom: 20,
  },
});

export default Registration;
