import React from 'react';
import { View, Text, StyleSheet, Image, TouchableOpacity } from 'react-native';
import { FontAwesome5 } from '@expo/vector-icons';
import { Feather } from '@expo/vector-icons';
import { useNavigation } from '@react-navigation/native';
import logo from '../assets/logo.png';
import desenho from '../assets/desenho.png';

const Home = () => {
  const navigation = useNavigation();
  return (
    <View style={styles.container}>
      <Image source={logo} style={styles.logo} />
      <FontAwesome5 style={styles.user} name="user-alt" size={42} color="white" />
      <Image source={desenho} style={styles.desenho} />

      <Text style={styles.txt}>Por que adotar?</Text>
      <Text style={styles.txt2}>Nesse exato</Text>
      <Text style={styles.txtl}>momento,</Text>
      <Text style={styles.txt3}>existem milhares de</Text>
      <Text style={styles.txt4}>doguinhos e gatinhos</Text>
      <Text style={styles.txt5}>esperando um</Text>
      <Text style={styles.txt6}>humano para chamar</Text>
      <Text style={styles.txtu}>de seu.</Text>

      <Text style={styles.txtsub}>Quem Somos?</Text>
      <Text style={styles.txt22}>somos uma ONGs sem fins</Text>
      <Text style={styles.txt23}>de lucro, a fim de ajudar aos</Text>
      <Text style={styles.txt24}>caes a achar um lar</Text>

      <TouchableOpacity style={styles.button} onPress={() => navigation.navigate('Login')}>
        <Text style={styles.buttontext}>Encontrar Meu Novo Amigo</Text>
      </TouchableOpacity>

      <TouchableOpacity style={styles.button_D}>
        <Feather style={styles.denun} name="phone" size={24} color="white" />
        <Text style={styles.buttontText2}>Denuncia</Text>
      </TouchableOpacity>
    </View>
  );
};

export default Home;

const styles = StyleSheet.create({
  container: {
    backgroundColor: '#000',
    flex: 1, // Usar flex para ajustar a altura em relação ao tamanho da tela
    alignItems: 'center', // Centralizar o conteúdo verticalmente
    justifyContent: 'center', // Centralizar o conteúdo horizontalmente
  },

  user: {
    position: 'absolute',
    top: '4%',
    right: '4%', // Mover para o canto superior direito
  },

  txt: {
    color: "#fff",
    position: 'absolute',
    top: '24%',
    left: '28.5%',
    fontSize: 20,
    fontWeight: 'bold',
  },
  txt2: {
    color: "#fff",
    position: 'absolute',
    top: '32%',
    left: '52%'
  },
  txtl: {
    color: "#fff",
    position: 'absolute',
    top: '34%',
    left: '53.5%'
  },
  txt3: {
    color: "#fff",
    position: 'absolute',
    top: '36%',
    left: '46%'
  },
  txt4: {
    color: "#fff",
    position: 'absolute',
    top: '38%',
    left: '45%'
  },
  txt5: {
    color: "#fff",
    position: 'absolute',
    top: '40%',
    left: '52%'
  },
  txt6: {
    color: "#fff",
    position: 'absolute',
    top: '42%',
    left: '46%'
  },
  txtu: {
    color: "#fff",
    position: 'absolute',
    top: '44%',
    left: '56%'
  },
  txtsub: {
    color: "#fff",
    position: 'absolute',
    top: '48%',
    left: '20%',
    fontWeight: 'bold',
    fontSize: 20,
  },

  button: {
    position: 'absolute',
    backgroundColor: '#ffffff',
    borderRadius: 50,
    width: 275,
    height: 40,
    alignSelf: 'center',
    bottom: '25%',
    alignItems: 'center',
    justifyContent: 'center',
  },

  buttontext: {
    fontSize: 20,
    textAlign: 'center',
    fontFamily: 'Open sans',
    color: '#000'
  },

  button_D: {
    position: 'absolute',
    backgroundColor: '#ffffff',
    borderRadius: 50,
    width: 275,
    height: 40,
    alignSelf: 'center',
    bottom: '18%',
    alignItems: 'center',
    justifyContent: 'center',
  },
  buttontText2: {
    fontSize: 20,
    textAlign: 'center',
    fontFamily: 'Open sans',
    color: '#000'
  },

  denun: {
    position: 'absolute',
    top: '18.5%',
    left: '22%'
  },

  logo: {
    width: '50%', // Tamanho relativo à largura da tela
    aspectRatio: 2, // Aspect ratio para manter a proporção
  },
  desenho: {
    width: '35%', // Tamanho relativo à largura da tela
    aspectRatio: 1, // Aspect ratio para manter a proporção
  },
});
