import { View, TextInput, Button, StyleSheet, Text, Image, TouchableOpacity } from 'react-native';
import React, { useState } from 'react';
import logo from '../assets/logo.png';
import { useNavigation } from '@react-navigation/native';
import { firebase } from '../config';

const CadastroUsuario = () => {
  const navigation = useNavigation();
  const [nome, setNome] = useState('');
  const [email, setEmail] = useState('');
  const [senha, setSenha] = useState('');

  const handleCadastro = () => {
    // Criar o usuário no Firebase Authentication
    firebase.auth().createUserWithEmailAndPassword(email, senha)
      .then((userCredential) => {
        // Cadastro bem-sucedido, agora você pode adicionar detalhes adicionais ao Realtime Database
        const user = userCredential.user;
        const userId = user.uid;
        const userEmail = user.email;

        // Referência para o nó de usuários no banco de dados
        const usersRef = firebase.database().ref("users");

        // Crie um novo ID para o usuário
        const novoUsuarioRef = usersRef.push();

        // Crie os dados do usuário
        const novoUsuarioDados = {
          nome: nome,
          email: email,
          userId: userId // Opcional: armazenar a ID do usuário para referência futura
        };

        // Salve os dados no Firebase Realtime Database
        novoUsuarioRef.set(novoUsuarioDados)
          .then(() => {
            console.log("Usuário cadastrado com sucesso!");
            // Redirecionar para outra tela após o cadastro bem-sucedido
            navigation.navigate('Login'); // Substitua 'Home' pelo nome da tela de destino
          })
          .catch((error) => {
            console.error("Erro ao cadastrar usuário no banco de dados:", error);
          });
      })
      .catch((error) => {
        console.error("Erro ao criar usuário:", error);
      });
  };

  return (
    <View style={styles.container}>
      <Image source={logo} style={styles.logo} />

      <Text style={styles.txt}>Crie uma conta </Text>
      <View style={styles.inputContainer}>
        <TextInput
          placeholder="Nome"
          onChangeText={setNome}
          value={nome}
          style={styles.textInput}
          placeholderTextColor="#000"
        />
        <View style={styles.separator} />
        <TextInput
          placeholder="Email"
          onChangeText={setEmail}
          value={email}
          style={styles.textInput}
          keyboardType="email-address"
          placeholderTextColor="#000"
        />
        <View style={styles.separator} />
        <TextInput
          placeholder="Senha"
          onChangeText={setSenha}
          value={senha}
          style={styles.textInput}
          secureTextEntry={true}
          placeholderTextColor="#000"
        />
      </View>
      
      <TouchableOpacity onPress={handleCadastro} style={styles.button}>
        <Text style={styles.buttonText}>Cadastrar</Text>
      </TouchableOpacity>

      <TouchableOpacity onPress={() => navigation.navigate('Login')} style={styles.signUpContainer}>
        <Text style={styles.signUpText}>Já tem uma conta?</Text>
        <Text style={styles.signUpLink}>Faça login agora</Text>
      </TouchableOpacity>
    </View>
  );
};

export default CadastroUsuario;

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#000',
    alignItems: 'center',
    justifyContent: 'center',
  },
  inputContainer: {
    width: '80%',
    backgroundColor: '#fff',
    borderRadius: 10,
    paddingHorizontal: 20,
    marginBottom: 20,
  },
  textInput: {
    height: 50,
    fontSize: 16,
    color: '#000',
    marginTop: 10,
  },
  separator: {
    height: 1,
    backgroundColor: '#000',
  },
  button: {
    height: 50,
    width: '60%',
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
    borderRadius: 25,
  },
  buttonText: {
    fontWeight: 'bold',
    fontSize: 18,
    color: '#000',
  },
  logo: {
    width: '50%',
    height: '25%',
    left: '1%',
    top: '-35%',
  },
  signUpContainer: {
    alignItems: 'center',
    marginTop: 20,
  },
  signUpText: {
    fontSize: 16,
    color: '#b8b8b8',
  },
  signUpLink: {
    fontSize: 18,
    color: '#fff',
    fontWeight: 'bold',
    marginTop: 5,
  },
  txt: {
    color: '#fff',
    fontSize: 40,
    fontWeight: 'bold',
    marginBottom: 30,
  },
});
