import React, { useState } from 'react';
import { View, Text, TextInput, TouchableOpacity, StyleSheet, Image, Button } from 'react-native';
import { getFirestore, collection, addDoc } from 'firebase/firestore';
import { useNavigation } from '@react-navigation/native';
import { launchImageLibrary } from 'react-native-image-picker'; // Importe a função para selecionar imagens
import { firebase } from '../config';
import ImagePicker from 'react-native-image-picker';

const Registration = () => {
  const [cidade, setCidade] = useState('');
  const [descricao, setDescricao] = useState('');
  const [telefone, setTelefone] = useState('');
  const [imageUri, setImageUri] = useState(null); // Estado para armazenar a URI da imagem

  const navigation = useNavigation();

  const handleCadastro = async () => {
    const db = getFirestore();
    try {
      await addDoc(collection(db, 'idrmp43D1TEUCWZWEHci'), {
        cidade,
        descricao,
        telefone,
        userEmail: firebase.auth().currentUser.email,
        imageUrl: imageUri, // Salve a URI da imagem no Firestore
      });
      console.log('Documento adicionado com sucesso!');
      // Limpar campos após o cadastro
      setCidade('');
      setDescricao('');
      setTelefone('');
      setImageUri(null); // Limpe a URI da imagem

      // Navegar para a tela de informações e passar os dados como parâmetros
      navigation.navigate('Dashboard');
    } catch (error) {
      console.error('Erro ao adicionar documento: ', error);
    }
  };

  const handleTelefoneChange = (text) => {
    // Remove tudo que não é número
    const formattedText = text.replace(/[^0-9]/g, '');
    setTelefone(formattedText);
  };

  const chooseImage = () => {
    const options = {
      mediaType: 'photo', // Tipo de mídia (pode ser 'photo' ou 'video')
      quality: 1, // Qualidade da imagem (0 a 1)
    };

    launchImageLibrary(options, (response) => {
      if (response.didCancel) {
        console.log('Seleção de imagem cancelada');
      } else if (response.error) {
        console.error('Erro ao selecionar imagem:', response.error);
      } else {
        // A imagem foi selecionada com sucesso, atualize o estado com a URI da imagem
        setImageUri(response.uri);
      }
    });
  };

  return (
    <View style={styles.container}>
      <Text style={styles.heading}>Cadastro de Informações</Text>
      <TextInput
        style={styles.input}
        placeholder="Cidade"
        placeholderTextColor="#fff"
        value={cidade}
        onChangeText={setCidade}
      />
      <TextInput
        style={styles.input}
        placeholder="Descrição"
        placeholderTextColor="#fff"
        value={descricao}
        onChangeText={setDescricao}
      />
      <TextInput
        style={styles.input}
        placeholder="Telefone"
        placeholderTextColor="#fff"
        value={telefone}
        onChangeText={handleTelefoneChange}
        keyboardType="numeric"
      />

      {/* Botão para selecionar uma imagem */}
      <TouchableOpacity style={styles.imageButton} onPress={chooseImage}>
        <Text style={styles.imageButtonText}>Selecionar Imagem</Text>
      </TouchableOpacity>

      {/* Exibe a imagem selecionada */}
      {imageUri && <Image source={{ uri: imageUri }} style={styles.selectedImage} />}

      <TouchableOpacity style={styles.button} onPress={handleCadastro}>
        <Text style={styles.buttonText}>Cadastrar</Text>
      </TouchableOpacity>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: '#000',
  },
  heading: {
    fontSize: 24,
    marginBottom: 20,
    color: '#fff',
  },
  input: {
    width: '80%',
    height: 40,
    borderColor: 'gray',
    borderWidth: 1,
    marginBottom: 10,
    padding: 10,
    color: '#fff',
  },
  button: {
    backgroundColor: '#fff',
    borderRadius: 25,
    width: '60%',
    height: 50,
    alignItems: 'center',
    justifyContent: 'center',
    marginTop: 20,
  },
  buttonText: {
    fontWeight: 'bold',
    fontSize: 18,
    color: '#000',
  },
  imageButton: {
    backgroundColor: '#fff',
    borderRadius: 25,
    width: '60%',
    height: 50,
    alignItems: 'center',
    justifyContent: 'center',
    marginTop: 20,
  },
  imageButtonText: {
    fontWeight: 'bold',
    fontSize: 16,
    color: '#000',
  },
  selectedImage: {
    width: 200,
    height: 200,
    resizeMode: 'contain',
    marginTop: 10,
  },
});

export default Registration;
