import React, { useState } from 'react';
import { View, Text, TextInput, TouchableOpacity, StyleSheet, Image } from 'react-native';
import { getFirestore, collection, addDoc } from 'firebase/firestore';
import { getStorage, ref, uploadString, getDownloadURL } from 'firebase/storage';
import { useNavigation } from '@react-navigation/native';
import { firebase } from '../config';
import { launchImageLibrary } from 'react-native-image-picker';
import * as ImagePicker from 'expo-image-picker';

const Registration = () => {
  const [cidade, setCidade] = useState('');
  const [descricao, setDescricao] = useState('');
  const [telefone, setTelefone] = useState('');
  const [imagemUrl, setImagemUrl] = useState(null);
  const navigation = useNavigation();

  const handleImageUpload = async () => {
    const { cancelled, uri } = await ImagePicker.launchImageLibraryAsync();
  
    if (!cancelled) {
      setImagemUrl(uri);
    }
  };

  const handleCadastro = async () => {
    const db = getFirestore();
    const storage = getStorage();
  
    try {
      const response = await fetch(imagemUrl);
      const blob = await response.blob();
  
      const imageRef = ref(storage, `images/${Math.random()}`);
      const snapshot = await uploadBytesResumable(imageRef, blob).then((snapshot) => {
        // Handle successful upload
        getDownloadURL(snapshot.ref).then((url) => {
          const imageUrl = url;
  
          addDoc(collection(db, 'idrmp43D1TEUCWZWEHci'), {
            cidade,
            descricao,
            telefone,
            imagemUrl: imageUrl,
            userEmail: firebase.auth().currentUser.email,
          })
            .then(() => {
              console.log('Documento adicionado com sucesso!');
              setCidade('');
              setDescricao('');
              setTelefone('');
              setImagemUrl(null);
              navigation.navigate('Dashboard');
            })
            .catch((error) => {
              console.error('Erro ao adicionar documento: ', error);
            });
        });
      });
    } catch (error) {
      console.error('Erro ao adicionar documento: ', error);
    }
  };
  
  

  return (
    <View style={styles.container}>
      <Text style={styles.heading}>Cadastro de Informações</Text>
      <TextInput
        style={styles.input}
        placeholder="Cidade"
        placeholderTextColor="#fff"
        value={cidade}
        onChangeText={setCidade}
      />
      <TextInput
        style={styles.input}
        placeholder="Descrição"
        placeholderTextColor="#fff"
        value={descricao}
        onChangeText={setDescricao}
      />
      <TextInput
        style={styles.input}
        placeholder="Telefone"
        placeholderTextColor="#fff"
        value={telefone}
        onChangeText={setTelefone}
        keyboardType="numeric"
      />
      {imagemUrl && (
        <Image
          source={{ uri: imagemUrl }}
          style={{ width: 100, height: 100, marginBottom: 10 }}
        />
      )}
      <TouchableOpacity style={styles.button} onPress={handleImageUpload}>
        <Text style={styles.buttonText}>Selecionar Imagem</Text>
      </TouchableOpacity>
      <TouchableOpacity style={styles.button} onPress={handleCadastro}>
        <Text style={styles.buttonText}>Cadastrar</Text>
      </TouchableOpacity>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: '#000',
  },
  heading: {
    fontSize: 24,
    marginBottom: 20,
    color: '#fff',
  },
  input: {
    width: '80%',
    height: 40,
    borderColor: 'gray',
    borderWidth: 1,
    marginBottom: 10,
    padding: 10,
    color: '#fff',
  },
  button: {
    backgroundColor: '#fff',
    borderRadius: 25,
    width: '60%',
    height: 50,
    alignItems: 'center',
    justifyContent: 'center',
    marginTop: 20,
  },
  buttonText: {
    fontWeight: 'bold',
    fontSize: 18,
    color: '#000',
  },
});

export default Registration;
