import React, { useEffect, useState } from 'react';
import { View, Text, StyleSheet, FlatList, TouchableOpacity } from 'react-native';
import { getFirestore, collection, getDocs } from 'firebase/firestore';
import { useNavigation } from '@react-navigation/native';
import Icon from 'react-native-vector-icons/Feather';

const Dashboard = () => {
  const navigation = useNavigation();
  const [data, setData] = useState([]); // Estado para armazenar os dados do Firestore

  // Função para buscar os dados do Firestore
  const fetchDataFromFirestore = async () => {
    const db = getFirestore();
    const dataCollection = collection(db, 'idrmp43D1TEUCWZWEHci'); // Substitua pelo nome da sua coleção
    try {
      const querySnapshot = await getDocs(dataCollection);
      const newData = [];
      querySnapshot.forEach((doc) => {
        // Adicione os dados do documento ao estado
        newData.push({ id: doc.id, ...doc.data() });
      });
      setData(newData);
    } catch (error) {
      console.error('Erro ao buscar dados do Firestore:', error);
    }
  };

  useEffect(() => {
    fetchDataFromFirestore(); // Chame a função de busca de dados quando o componente for montado
  }, []);

  const goToRegistration = () => {
    navigation.navigate('Registration'); // Navegue para a tela 'Registration'
  };

  return (
    <View style={styles.container}>
      {/* Ícone de mais no canto superior direito */}
      <TouchableOpacity onPress={goToRegistration} style={styles.iconContainer}>
        <Icon name="plus" size={30} color="#fff" />
      </TouchableOpacity>
      
      <Text style={styles.heading}>Dados Cadastrados</Text>
      <FlatList
        data={data}
        keyExtractor={(item) => item.id}
        renderItem={({ item }) => (
          <View style={styles.itemContainer}>
            <Text style={styles.itemText}>Cidade: {item.cidade}</Text>
            <Text style={styles.itemText}>Descrição: {item.descricao}</Text>
            <Text style={styles.itemText}>Telefone: {item.telefone}</Text>
            {/* Adicione outros campos aqui conforme necessário */}
          </View>
        )}
      />
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#000',
    paddingTop: 20,
    paddingHorizontal: 20,
  },
  heading: {
    fontSize: 24,
    marginBottom: 20,
    color: '#fff',
  },
  iconContainer: {
    position: 'absolute',
    top: 20,
    right: 20,
  },
  itemContainer: {
    backgroundColor: '#fff',
    borderRadius: 10,
    padding: 16,
    marginBottom: 10,
    width: '100%',
  },
  itemText: {
    fontSize: 16,
    marginBottom: 8,
    color: '#000',
  },
});

export default Dashboard;
