import React, { useEffect, useState } from 'react';
import { View, Text, StyleSheet, FlatList, TouchableOpacity, Image, Button } from 'react-native';
import { getFirestore, collection, getDocs } from 'firebase/firestore';
import { useNavigation } from '@react-navigation/native';
import Icon from 'react-native-vector-icons/Feather';
import { firebase } from '../config';

const Dashboard = () => {
  const navigation = useNavigation();
  const [data, setData] = useState([]); // Estado para armazenar os dados do Firestore
  const [userEmail, setUserEmail ] = useState(null);

  // Função para buscar os dados do Firestore
  const fetchDataFromFirestore = async () => {
    const db = getFirestore();
    const dataCollection = collection(db, 'idrmp43D1TEUCWZWEHci'); // Substitua pelo nome da sua coleção

    try {
      const querySnapshot = await getDocs(dataCollection);
      const newData = [];
      querySnapshot.forEach((doc) => {
        // Adicione os dados do documento ao estado
        newData.push({ id: doc.id, ...doc.data() });
      });
      setData(newData);
    } catch (error) {
      console.error('Erro ao buscar dados do Firestore:', error);
    }
  };
  
  useEffect(() => {
    fetchDataFromFirestore(); // Chame a função de busca de dados quando o componente for montado
    const currentUser = firebase.auth().currentUser;

    if (currentUser){
      setUserEmail(currentUser.email);
    }

  }, []);

  const goToRegistration = () => {
    navigation.navigate('Registration'); // Navegue para a tela 'Registration'
  };

  const handleRefresh = () => {
    fetchDataFromFirestore(); // Chame a função de busca de dados para atualizar a página
  };

  return (
    <View style={styles.container}>
   
      <TouchableOpacity onPress={goToRegistration} style={styles.iconContainer}>
        <Icon name="plus" size={30} color="#fff" />
      </TouchableOpacity>

      <Text style={styles.heading}>Feed</Text>

      <Button title="Atualizar" onPress={handleRefresh} />

      <FlatList
        data={data}
        keyExtractor={(item) => item.id}
        renderItem={({ item }) => (
          <View style={styles.feedItem}>
            <View style={styles.feedHeader}>
              <Text style={styles.username}>{item.userEmail}</Text>
              <Icon name="more-horizontal" size={20} color="#fff" />
            </View>
            {item.imagemUri && (
              <Image source={{ uri: item.imagemUri }} style={styles.imagem} />
            )}
            <Text style={styles.itemText}>Cidade: {item.cidade}</Text>
            <Text style={styles.itemText}>Descrição: {item.descricao}</Text>
            <Text style={styles.itemText}>Telefone: {item.telefone}</Text>
            <View style={styles.actions}>
              <TouchableOpacity>
                <Icon name="heart" size={20} color="#fff" />
              </TouchableOpacity>
              <TouchableOpacity>
                <Icon name="message-circle" size={20} color="#fff" />
              </TouchableOpacity>
              <TouchableOpacity>
                <Icon name="send" size={20} color="#fff" />
              </TouchableOpacity>
            </View>
          </View>
        )}
      />
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#000', // Fundo preto
  },
  heading: {
    fontSize: 24,
    margin: 20,
    color: '#fff', // Texto branco
  },
  iconContainer: {
    position: 'absolute',
    top: 20,
    right: 20,
  },
  feedItem: {
    backgroundColor: '#111', // Fundo escuro
    borderBottomWidth: 1,
    borderColor: '#333', // Bordas mais escuras
    padding: 16,
  },
  feedHeader: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
  },
  username: {
    fontSize: 16,
    fontWeight: 'bold',
    marginRight: 8,
    color: '#fff', // Texto branco
  },
  itemText: {
    fontSize: 16,
    marginVertical: 8,
    color: '#fff', // Texto branco
  },
  actions: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    marginTop: 8,
  },
  imagem: {
    width: 200,
    height: 200,
    marginBottom: 20,
  },
});

export default Dashboard;
