import React, { useState } from 'react';
import { View, Text, TextInput, TouchableOpacity, StyleSheet } from 'react-native';
import { getFirestore, collection, addDoc } from 'firebase/firestore';
import { useNavigation } from '@react-navigation/native';

const Todo = () => {
  const [todo, setTodo] = useState("")
 
  const addTodo = (e) => {
      e.preventDefault();        
  }

  return (
      <section className="todo-container">
          <div className="todo">
              <h1 className="header">
                  Todo-App
              </h1>
 
              <div>
 
                  <div>
                      <input
                          type="text"
                          placeholder="What do you have to do today?"
                          onChange={(e)=>setTodo(e.target.value)}
                      />
                  </div>
 
                  <div className="btn-container">
                      <button
                          type="submit"
                          className="btn"
                          onClick={addTodo}
                      >
                          Submit
                      </button>
                  </div>
 
              </div>
 
              <div className="todo-content">
                  ...
              </div>
          </div>
      </section>
  )
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: '#000',
  },
  heading: {
    fontSize: 24,
    marginBottom: 20,
    color: '#fff',
  },
  input: {
    width: '80%',
    height: 40,
    borderColor: 'gray',
    borderWidth: 1,
    marginBottom: 10,
    padding: 10,
    color: '#fff',
  },
  button: {
    backgroundColor: '#fff',
    borderRadius: 25,
    width: '60%',
    height: 50,
    alignItems: 'center',
    justifyContent: 'center',
    marginTop: 20,
  },
  buttonText: {
    fontWeight: 'bold',
    fontSize: 18,
    color: '#000',
  },
  signUpContainer: {
    flexDirection: 'row',
    alignItems: 'center',
    marginTop: 10,
  },
  signUpText: {
    fontSize: 16,
    color: '#b8b8b8',
    marginRight: 5,
  },
  signUpLink: {
    fontSize: 18,
    color: '#fff',
    fontWeight: 'bold',
  },
});

export default Registration;
