import React, { useState, useEffect } from 'react';
import { View, Text, FlatList, StyleSheet, Image, TouchableOpacity } from 'react-native';
import { useNavigation } from '@react-navigation/native';
import { getFirestore, collection, getDocs } from 'firebase/firestore'; // Importe as funções necessárias do Firebase

const Dashboard = () => {
  const navigation = useNavigation();
  const [dogs, setDogs] = useState([]);

  useEffect(() => {
    const fetchDogs = async () => {
      try {
        const db = getFirestore(); // Obtém a instância do Firestore
        const dogsCollection = collection(db, 'dogs');
        const dogsSnapshot = await getDocs(dogsCollection);
        const dogsData = dogsSnapshot.docs.map((doc) => doc.data());
        setDogs(dogsData);
      } catch (error) {
        console.error('Erro ao buscar cachorros', error);
      }
    };

    fetchDogs();
  }, []);

  const renderDog = ({ item }) => (
    <TouchableOpacity style={styles.dogContainer} onPress={() => showMessage(`Telefone: ${item.phone}\nCidade: ${item.city}`)}>
      {item.imageUrl && <Image source={{ uri: item.imageUrl }} style={styles.dogImage} />}
      <Text style={styles.dogDescription}>{item.description}</Text>
      <Text style={styles.dogPhone}>Telefone: {item.phone}</Text>
      <Text style={styles.dogCity}>Cidade: {item.city}</Text>
    </TouchableOpacity>
  );

  const showMessage = (message) => {
    Alert.alert('Detalhes do Cachorro', message);
  };

  return (
    <View style={styles.container}>
      <Text style={styles.title}>Cachorros Cadastrados</Text>
      <FlatList
        data={dogs}
        keyExtractor={(item, index) => index.toString()}
        renderItem={renderDog}
      />
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#000',
    justifyContent: 'center',
    alignItems: 'center',
  },
  title: {
    color: '#fff',
    fontSize: 20,
    fontWeight: 'bold',
    marginBottom: 20,
  },
  dogContainer: {
    backgroundColor: '#333',
    padding: 10,
    borderRadius: 5,
    marginBottom: 10,
  },
  dogImage: {
    width: 150,
    height: 150,
    marginBottom: 10,
    borderRadius: 5,
  },
  dogDescription: {
    color: '#fff',
    fontSize: 16,
    marginBottom: 5,
  },
  dogPhone: {
    color: '#fff',
    fontSize: 14,
  },
  dogCity: {
    color: '#fff',
    fontSize: 14,
  },
});

export default Dashboard;
