import React from 'react';
import { View, Text, StyleSheet, TouchableOpacity, ImageBackground, I } from 'react-native';
import { Feather } from '@expo/vector-icons';
import { useNavigation } from '@react-navigation/native';
import logo from '../assets/logo.png';

const Home = () => {
  const navigation = useNavigation();

  return (
    <View style={styles.container}>
      <ImageBackground
        source={{
          uri:
            'https://i.pinimg.com/originals/d2/99/40/d2994005233783287041f6b90980546b.gif',
        }}
        style={styles.background}
      >
        <Image source={logo} style={styles.logo} />

        <Text style={styles.title}>Por que adotar?</Text>
        <Text style={styles.description}>
          Nesse exato momento, existem milhares de doguinhos e gatinhos esperando um humano para chamar de seu.
        </Text>

        <TouchableOpacity
          style={styles.button}
          onPress={() => navigation.navigate('Login')}
        >
          <Text style={styles.buttonText}>Encontrar Meu Novo Amigo</Text>
        </TouchableOpacity>

        <TouchableOpacity
          style={styles.reportButton}
          onPress={() => navigation.navigate('DenunciaCachorro')}
        >
          <Feather name="phone" size={24} color="white" style={styles.reportIcon} />
          <Text style={styles.reportButtonText}>Denunciar</Text>
        </TouchableOpacity>
      </ImageBackground>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: 'black',
    alignItems: 'center',
    justifyContent: 'center',
    padding: 20,
  },
  background: {
    position: 'relative', // Mudamos para relativo para permitir sobreposições corretas
    width: '100%',
    height: '100%', // Usamos 100% para preencher a tela inteira
    opacity: 0.7,
  },
  logo: {
    width: 220,
    height: 150,
    top: -200,
    marginBottom: 20,
  },
  title: {
    top: -200,
    fontSize: 24,
    fontWeight: 'bold',
    color: '#fff',
  },
  description: {
    top: -190,
    fontSize: 16,
    color: '#fff',
    textAlign: 'center',
    marginBottom: 10,
  },
  button: {
    backgroundColor: '#fff',
    borderRadius: 25,
    width: 250,
    height: 50,
    alignItems: 'center',
    justifyContent: 'center',
    marginTop: 20,
  },
  buttonText: {
    fontSize: 18,
    fontWeight: 'bold',
    color: '#000',
  },
  reportButton: {
    flexDirection: 'row',
    backgroundColor: 'transparent',
    borderWidth: 2,
    borderColor: 'white',
    borderRadius: 25,
    width: 250,
    height: 50,
    alignItems: 'center',
    justifyContent: 'center',
    marginTop: 10,
  },
  reportIcon: {
    marginRight: 10,
  },
  reportButtonText: {
    fontSize: 18,
    fontWeight: 'bold',
    color: 'white',
  },
});

export default Home;
