import React from 'react';
import { View, Text, FlatList, StyleSheet, Image, TouchableOpacity } from 'react-native';
import { useNavigation } from '@react-navigation/native';

const Dashboard = ({ posts, onAddPost }) => {
  const navigation = useNavigation();

  const renderPost = ({ item }) => (
    <View style={styles.dogContainer}>
      {item.imageUri && <Image source={{ uri: item.imageUri }} style={styles.dogImage} />}
      <Text style={styles.dogDescription}>{item.name}</Text>
      <Text style={styles.dogPhone}>Telefone: {item.breed}</Text>
      <Text style={styles.dogCity}>Cidade: {item.age}</Text>
    </View>
  );

  return (
    <View style={styles.container}>
      <Text style={styles.title}>Cachorros Cadastrados</Text>
      <FlatList
        data={posts}
        keyExtractor={(item, index) => index.toString()}
        renderItem={renderPost}
      />
      <TouchableOpacity
        style={styles.addButton}
        onPress={() => navigation.navigate('Registration', { onAddPost })}
      >
        <Text style={styles.addButtonText}>Adicionar Postagem</Text>
      </TouchableOpacity>
    </View>
  );
};



const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#000',
    justifyContent: 'center',
    alignItems: 'center',
  },
  title: {
    color: '#fff',
    fontSize: 20,
    fontWeight: 'bold',
    marginBottom: 20,
  },
  dogContainer: {
    backgroundColor: '#333',
    padding: 10,
    borderRadius: 5,
    marginBottom: 10,
  },
  dogImage: {
    width: 150,
    height: 150,
    marginBottom: 10,
    borderRadius: 5,
  },
  addButton: {
    backgroundColor: '#007BFF',
    padding: 10,
    borderRadius: 5,
    marginTop: 20,
  },
  addButtonText: {
    color: '#fff',
    fontWeight: 'bold',
    textAlign: 'center',
  },
});

export default Dashboard;
