import React, { useEffect, useState } from 'react';
import { View, TouchableOpacity, StyleSheet, Text, FlatList } from 'react-native';
import { useNavigation } from '@react-navigation/native';
import Icon from 'react-native-vector-icons/Feather';
import firestore from '@react-native-firebase/firestore'; // Importe o módulo Firestore


const Dashboard = () => {
  const navigation = useNavigation();
  const [data, setData] = useState([]); // Estado para armazenar os dados do Firebase

  // Função para buscar os dados do Firebase
  const fetchDataFromFirebase = async () => {
    const collectionRef = firestore().collection('idrmp43D1TEUCWZWEHci'); // Substitua pelo nome da sua coleção no Firestore
    try {
      const snapshot = await collectionRef.get();
      const newData = snapshot.docs.map(doc => doc.data());
      setData(newData); // Atualize o estado com os dados buscados
    } catch (error) {
      console.error('Erro ao buscar dados do Firebase:', error);
    }
  };

  useEffect(() => {
    fetchDataFromFirebase(); // Chame a função de busca de dados quando o componente for montado
  }, []);

  const goToRegistration = () => {
    navigation.navigate('Registration'); // Nome da tela de registro no Stack Navigator
  };

  return (
    <View style={styles.container}>
      <TouchableOpacity onPress={goToRegistration} style={styles.iconContainer}>
        <Icon name="plus" size={30} color="#FFF" />
      </TouchableOpacity>
      
      <FlatList
        data={data} // Use os dados buscados para renderizar a lista
        keyExtractor={(item, index) => index.toString()}
        renderItem={({ item }) => (
          <View style={styles.itemContainer}>
            <Text style={styles.itemText}>{item.cidade}</Text>
            {/* Adicione outros campos aqui conforme necessário */}
          </View>
        )}
      />
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#000',
    alignItems: 'flex-start',
    justifyContent: 'flex-start',
  },
  iconContainer: {
    position: 'absolute',
    top: 20,
    left: 20,
  },
  itemContainer: {
    padding: 16,
    borderBottomWidth: 1,
    borderBottomColor: '#DDD',
  },
  itemText: {
    color: '#FFF', // Estilo de texto branco
    fontSize: 16,
  },
});

export default Dashboard;
