import React, { useState, useEffect } from 'react';
import { View, Text, FlatList, StyleSheet, Image, TouchableOpacity } from 'react-native';
import { useNavigation } from '@react-navigation/native';
import { getFirestore, collection, getDocs, query, where } from 'firebase/firestore';
import { firebase } from '../config'; // Importação do Firebase

const Dashboard = () => {
  const navigation = useNavigation();
  const [dogs, setDogs] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const db = getFirestore();
        const dogsCollection = collection(db, 'dogs');
        const dogsQuery = query(dogsCollection, where('userId', '==', firebase.auth().currentUser.uid));
        const dogsSnapshot = await getDocs(dogsQuery);
        const dogsData = dogsSnapshot.docs.map((doc) => ({ id: doc.id, ...doc.data() }));
        setDogs(dogsData);
      } catch (error) {
        console.error('Erro ao buscar dados', error);
        showMessage('Erro ao buscar dados.');
      }
    };

    fetchData();
  }, []);

  const showMessage = (message) => {
    Alert.alert('Mensagem', message);
  };

  return (
    <View style={styles.container}>
      <TouchableOpacity
        style={styles.registrationButton}
        onPress={() => navigation.navigate('Registration')}
      >
        <Text style={styles.registrationButtonText} >Cadastrar Novo Cachorro</Text>
      </TouchableOpacity>
      <Text style={styles.title}>Cachorros Cadastrados</Text>
      <FlatList
        data={dogs}
        keyExtractor={(item) => item.id}
        renderItem={({ item }) => (
          <TouchableOpacity
            style={styles.dogContainer}
            onPress={() => showMessage(`Telefone: ${item.phone}\nCidade: ${item.city}`)}
          >
            {item.imageUrl && <Image source={{ uri: item.imageUrl }} style={styles.dogImage} />}
            <Text style={styles.dogDescription}>{item.description}</Text>
          </TouchableOpacity>
        )}
      />
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#000',
    justifyContent: 'center',
    alignItems: 'center',
  },
  title: {
    color: '#fff',
    fontSize: 20,
    fontWeight: 'bold',
    marginBottom: 20,
  },
  dogContainer: {
    backgroundColor: '#333',
    padding: 10,
    borderRadius: 5,
    marginBottom: 10,
  },
  dogImage: {
    width: 150,
    height: 150,
    marginBottom: 10,
    borderRadius: 5,
  },
  dogDescription: {
    color: '#fff',
    fontSize: 16,
    marginBottom: 5,
  },
});

export default Dashboard;
