import React, { useState } from 'react';
import { View, Text, TextInput, TouchableOpacity, StyleSheet, Image, Button } from 'react-native';
import { getFirestore, collection, addDoc, ref, putFile, getDownloadURL, updateDoc, doc } from 'firebase/firestore';
import { useNavigation } from '@react-navigation/native';
import { firebase } from '../config';
import ImagePicker from 'react-native-image-picker';

const Registration = () => {
  const [cidade, setCidade] = useState('');
  const [descricao, setDescricao] = useState('');
  const [telefone, setTelefone] = useState('');
  const [imageUri, setImageUri] = useState(null);

  const navigation = useNavigation();

  const chooseImage = () => {
    ImagePicker.launchImageLibrary({}, (response) => {
      if (response.uri) {
        setImageUri(response.uri);
      }
    });
  };

  const handleCadastro = async () => {
    const db = getFirestore();
    try {
      const docRef = await addDoc(collection(db, 'idrmp43D1TEUCWZWEHci'), {
        cidade,
        descricao,
        telefone,
        imageUrl: '',
        userEmail: firebase.auth().currentUser.email,
      });

      if (imageUri) {
        const storageRef = ref(storage, `images/${docRef.id}`);
        await putFile(storageRef, imageUri);

        const imageUrl = await getDownloadURL(storageRef);

        await updateDoc(doc(db, 'idrmp43D1TEUCWZWEHci', docRef.id), {
          imageUrl,
        });
      }

      console.log('Documento adicionado com sucesso!');
      setCidade('');
      setDescricao('');
      setTelefone('');
      setImageUri(null);
      navigation.navigate('Dashboard');
    } catch (error) {
      console.error('Erro ao adicionar documento: ', error);
    }
  };

  return (
    <View style={styles.container}>
      <Text style={styles.heading}>Cadastro de Informações</Text>
      <TextInput
        style={styles.input}
        placeholder="Cidade"
        placeholderTextColor="#fff"
        value={cidade}
        onChangeText={setCidade}
      />
      <TextInput
        style={styles.input}
        placeholder="Descrição"
        placeholderTextColor="#fff"
        value={descricao}
        onChangeText={setDescricao}
      />
      <TextInput
        style={styles.input}
        placeholder="Telefone"
        placeholderTextColor="#fff"
        value={telefone}
        onChangeText={setTelefone}
        keyboardType="numeric"
      />
      {imageUri && <Image source={{ uri: imageUri }} style={styles.image} />}
      <TouchableOpacity style={styles.chooseButton} onPress={chooseImage}>
        <Text style={styles.buttonText}>Escolher Imagem</Text>
      </TouchableOpacity>
      <TouchableOpacity style={styles.button} onPress={handleCadastro}>
        <Text style={styles.buttonText}>Cadastrar</Text>
      </TouchableOpacity>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: '#000',
  },
  heading: {
    fontSize: 24,
    marginBottom: 20,
    color: '#fff',
  },
  input: {
    width: '80%',
    height: 40,
    borderColor: 'gray',
    borderWidth: 1,
    marginBottom: 10,
    padding: 10,
    color: '#fff',
  },
  button: {
    backgroundColor: '#fff',
    borderRadius: 25,
    width: '60%',
    height: 50,
    alignItems: 'center',
    justifyContent: 'center',
    marginTop: 20,
  },
  chooseButton: {
    backgroundColor: '#fff',
    borderRadius: 25,
    width: '60%',
    height: 50,
    alignItems: 'center',
    justifyContent: 'center',
    marginTop: 20,
  },
  buttonText: {
    fontWeight: 'bold',
    fontSize: 18,
    color: '#000',
  },
  image: {
    width: 200,
    height: 200,
    resizeMode: 'contain',
  },
});

export default Registration;
