import React, { useState } from 'react';
import {
  View,
  Text,
  TouchableOpacity,
  TextInput,
  StyleSheet,
  Image,
  Alert,
} from 'react-native';
import { useNavigation } from '@react-navigation/native';
import { MaterialCommunityIcons } from '@expo/vector-icons';
import * as ImagePicker from 'expo-image-picker';
import { getFirestore, collection, addDoc, f } from 'firebase/firestore'; // Importe as funções necessárias do Firebase

const PostScreen = () => {
  const navigation = useNavigation();
  const [description, setDescription] = useState('');
  const [phone, setPhone] = useState('');
  const [city, setCity] = useState('');
  const [imageUri, setImageUri] = useState(null);

  const handlePost = async () => {
    if (description && phone && city && imageUri) {
      try {
        const db = getFirestore(); // Obtém a instância do Firestore
        const user = firebase.auth().currentUser;

        if (!user) {
          showMessage('Usuário não logado.');
          return;
        }

        // Adiciona o documento com os dados do cachorro na coleção "dogs"
        await addDoc(collection(db, 'dogs'), {
          userId: user.uid,
          description: description,
          phone: phone,
          city: city,
          imageUrl: imageUri,
        });

        showMessage('Cachorro cadastrado com sucesso!');
        setDescription('');
        setPhone('');
        setCity('');
        setImageUri(null);
      } catch (error) {
        console.error('Erro ao cadastrar cachorro', error);
        showMessage('Erro ao cadastrar cachorro.');
      }
    } else {
      showMessage('Preencha todos os campos.');
    }
  };

  const pickImage = async () => {
    const permissionResult = await ImagePicker.requestMediaLibraryPermissionsAsync();
    if (!permissionResult.granted) {
      showMessage('Permissão para acessar a galeria negada.');
      return;
    }

    const imagePickerResult = await ImagePicker.launchImageLibraryAsync({
      mediaTypes: ImagePicker.MediaTypeOptions.Images,
      allowsEditing: true,
      aspect: [4, 3],
      quality: 1,
    });

    if (!imagePickerResult.cancelled) {
      setImageUri(imagePickerResult.uri);
    }
  };

  const showMessage = (message) => {
    Alert.alert('Mensagem', message);
  };

  return (
    <View style={styles.container}>
      {imageUri && <Image source={{ uri: imageUri }} style={styles.image} />}

      <TouchableOpacity style={styles.uploadButton} onPress={pickImage}>
        <MaterialCommunityIcons name="camera" size={24} color="#fff" />
        <Text style={styles.uploadButtonText}>Upload Image</Text>
      </TouchableOpacity>

      <TextInput
        style={styles.input}
        placeholder="Description"
        value={description}
        onChangeText={setDescription}
        placeholderTextColor="#fff"
      />

      <TextInput
        style={styles.input}
        placeholder="Phone Number"
        value={phone}
        onChangeText={setPhone}
        keyboardType="numeric"
        placeholderTextColor="#fff"
      />

      <TextInput
        style={styles.input}
        placeholder="City"
        value={city}
        onChangeText={setCity}
        placeholderTextColor="#fff"
      />

      <TouchableOpacity style={styles.postButton} onPress={handlePost}>
        <Text style={styles.postButtonText}>Post</Text>
      </TouchableOpacity>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: '#000',
    paddingHorizontal: 20,
  },
  image: {
    width: 200,
    height: 200,
    marginBottom: 20,
    borderRadius: 5,
  },
  uploadButton: {
    flexDirection: 'row',
    backgroundColor: '#007BFF',
    padding: 10,
    borderRadius: 5,
    marginBottom: 20,
    alignItems: 'center',
  },
  uploadButtonText: {
    color: '#fff',
    fontWeight: 'bold',
    marginLeft: 5,
  },
  input: {
    borderWidth: 1,
    borderColor: 'gray',
    padding: 10,
    width: '100%',
    marginBottom: 20,
    color: '#fff',
    borderRadius: 5,
  },
  postButton: {
    backgroundColor: '#28A745',
    padding: 10,
    borderRadius: 5,
    width: '100%',
    alignItems: 'center',
  },
  postButtonText: {
    color: '#fff',
    fontWeight: 'bold',
  },
});

export default PostScreen;
