import { View, Text, TouchableOpacity,TextInput, StyleSheet} from 'react-native'
import React,{useState} from 'react'

import {useNavigation}  from '@react-navigation/native'
import { firebase } from '../config'


const Login = () => {
    const navigation = useNavigation()
    const [email, setEmail] = useState('')
    const [password, setPassoword] = useState('')

    loginUser = async (email, password) => {
        try {
            await firebase.auth().signInWithEmailAndPassword(email,password)
        } catch (error){
            alert("Usuário e Senha Incorreta!")
        }
    }

    return(
        <View style={styles.container}>
             <Text style={styles.txt}>Acesse</Text>
            <Text style={{fontWeight:'bold', fontSize:26,}}></Text>
            <View style={{marginTop:40}}>
                
                <TextInput
                    style={styles.textInput} 
                    placeholder="Usuário" 
                    onChangeText={(email) => setEmail(email)}     
                    autoCapitalize='none'  
                    autoCorrect={false}       
                />
                <TextInput
                    style={styles.textInput}
                    placeholder="Senha" 
                    onChangeText={(password) => setPassoword(password)}     
                    autoCapitalize='none'  
                    autoCorrect={false} 
                    secureTextEntry={true}  
                        
                />
            </View>
            <TouchableOpacity
                onPress={() => loginUser(email, password)}
                style={styles.button}
                
            >
                <Text  style={{fontWeight:'bold',fontSize:22,color:'white'}}> Sing Up </Text>
            </TouchableOpacity>

            <TouchableOpacity
                onPress={() => navigation.navigate('Registration')}
                style={{marginTop:20}}
            >
                <Text  style={{fontWeight:'bold',fontSize:16,position:'relative',left:'-13%',top:'98%',color:'white'}}> Não Tem Uma Conta?</Text>
                <Text  style={{fontWeight:'bold',fontSize:16, color:'#0062F5',top:'49%',position:'relative',left:'28%'}}> Cadastre-se já </Text>
            </TouchableOpacity>
        </View>
    )
  
}

export default Login

const styles = StyleSheet.create({
    container:{
        
        alignItems:'center',
        backgroundColor:'#000',
        width:'100%',
        height:'100%'
        
    },
    textInput:{
        top:'25%',
        
        borderWidth: 5,
        left:'3%',
      color:'black',
      fontSize:22,
      marginTop:'2%',
      marginBottom:'10%',
      paddingStart:'55%',
      borderRadius:50,
      backgroundColor:'#fff',
      position:'relative',
      fontFamily:'open sans',
      height:'20%',
      
      
      
      
    },
    button:{
        marginBottom:50,
        height:50,
        width:150,
        backgroundColor:'#026efd',
        alignItems:'center',
        justifyContent:'center',
        borderRadius:50,
        position:'absolute',
        top:'61%',
       left:'31%',
        


    },
    txt:{
        color:"#fff",
        position:'absolute',
        top:'11%',
        left:'5%',
        fontSize:30,
        fontFamily:'open sans',
        fontWeight:'bold'
        
        
      },
  
})
